sys_script.script:
/*
 * Script ensures that at maximum only 1 record in the portfolio (x_708650_pfm_portfolio) table exists per user
 *
 * Revision History
 * JR - 10/11/2021 - Initial Script Creation
*/

(function executeRule(current, previous /*null when async*/) {

	// Initialize variables
	var exisitingRecord = "";
	var portfolioUrl = "";
	var link = "";

	// Check if an exisiting portfolio exists for the logged-in user
	var grPortfolio = new GlideRecord("x_708650_pfm_portfolio");
	grPortfolio.addQuery('user', gs.getUserID());
	grPortfolio.query();
	if(grPortfolio.next() && grPortfolio.sys_id != current.sys_id){

		// If a portfolio exists, create a URL to link to the exisiting portfolio record
		exisitingRecord = grPortfolio.getUniqueValue();
		portfolioUrl = "/x_708650_pfm_portfolio?sys_id=" + exisitingRecord;
		link = '<a href="' + portfolioUrl + '">here</a>';
		var message = gs.getMessage('A portfolio already exists for this user. Please click ') + link + gs.getMessage(' to access the exisiting portfolio.');
		gs.addErrorMessage(message);
		current.setAbortAction(true);
	}

})(current, previous);
